import { createContext, useState } from "react";
import run from "../config/dhatchan";

export const Context = createContext();

const ContextProvider = (props) => {
  const [input, settInput] = useState("");
  const [recent, setRecent] = useState("");
  const [prevPrompt, setPrevPrompt] = useState([]); // stores history [{prompt, result}]
  const [showResult, setShowResult] = useState(false);
  const [loading, setLoading] = useState(false);
  const [resultData, setResultData] = useState("");

  const delayPara = (index, nextWord) => {
    setTimeout(function () {
      setResultData((prev) => prev + nextWord);
    }, 75 * index);
  };

  const onSent = async (prompt) => {
    const finalPrompt = prompt || input;

    setResultData("");
    setLoading(true);
    setShowResult(true);
    setRecent(finalPrompt);

    // Call API
    const response = await run(finalPrompt);

    let responseArray = response.split("**");
    let newResponse = "";
    for (let i = 0; i < responseArray.length; i++) {
      if (i === 0 || i % 2 !== 1) {
        newResponse += responseArray[i];
      } else {
        newResponse += "<b>" + responseArray[i] + "</b>";
      }
    }

    let newResponse2 = newResponse.split("*").join("<br/>");
    let newResponseArray = newResponse2.split(" ");
    for (let i = 0; i < newResponseArray.length; i++) {
      const nextWord = newResponseArray[i];
      delayPara(i, nextWord + " ");
    }

    // Save to history with prompt + answer
    setPrevPrompt((prev) => [...prev, { prompt: finalPrompt, result: newResponse2 }]);

    setResultData(newResponse2);
    setLoading(false);
    settInput("");
  };

  // Function to load old prompt without calling API
  const loadPrompt = (item) => {
    setRecent(item.prompt);
    setResultData(item.result);
    setShowResult(true);
  };

  const contextValue = {
    prevPrompt,
    setPrevPrompt,
    onSent,
    loadPrompt, // expose loader
    setRecent,
    recent,
    showResult,
    loading,
    resultData,
    input,
    settInput,
  };

  return <Context.Provider value={contextValue}>{props.children}</Context.Provider>;
};

export default ContextProvider;
